mapper.select:
SELECT id, userId, orderSN, order_status orderStatus, payment_status paymentStatus, address, totalPrice, postFee, payFee, youhuis, coupon_name couponName, coupon_money couponMoney, user_coupon_id userCouponId, remark FROM tb_order WHERE active = 0
and order_status =#{orderStatus}
ORDER BY id DESC limit #{pageNumber},#{pageSize}
